package suzy_java_task;

public class Class01_task1_concatination {
	


	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		int + int + int

        int a1= 15;
        int a2 = 10;
        int a3 = 20;
        System.out.println(a1 + a2 + a3);
	

// 2. int + String + int 
        int a4 =11;
        String a5 = " big money  ";
        int a6 = 10;
        System.out.println(a4 + a5 + a6);

// 3. float + double + int
        
        float a7= 9.5f;
        double a8 = 7.2;
        int a9 = 45;
        System.out.println(a7 + a8 +a9);
        
        
//4.String+Float+int 
        String b1= "money";
        float b2 =6.9f;
        int b3= 50;
        System.out.println(b1+b2+b3);
        
        
//5.float + int + String
        float b4 = 22.50f;
        int b5 = 300;
        String emily = "happy life  ";
        System.out.println(b4 + b5 + emily);
        
//String + int + double + String
        String scenario = " be happy ";
        int c5 = 10;
        double c6 = 79.9;
        String big = " be strong";

        System.out.println(scenario + c5 + c6 + big );

// . String + int + String + double
        String b9 = " dont be sad ";
        int b0 = 200;
        String b10 = " go away";
        double b11 = 69.5;

        System.out.println(b9 + b0 + b10 + b11);
        
        
// . int + int + String + (int + int + int)
        int c1 = 50;
        int c2 = 100;
        int c3 = 150;
        String easy = " freedom ";
        int add = c1+c2+c3;
        System.out.println(c1 +c2 + easy + add);
	}
}

	